sys_ui_script.script:
var CACHE_NAME = "cc19-cache-v1";
var urlsToPreCache = ["/venitian_map.png", "/coolconf_icon.png"]; // URLs you want to precache

self.addEventListener("install", function(event) {
  event.waitUntil(
    caches.open(CACHE_NAME).then(function(cache) {
      console.log("SW - Opened cache");
      return cache.addAll(urlsToPreCache);
    })
  );
});

self.addEventListener("fetch", function(event) {
  console.log("SW - handling a network request done by the browser");
  event.respondWith(
    caches
      .open(CACHE_NAME)
      .then(function(cache) {
        return cache.match(event.request);
      })
      .then(function(response) {
        if (response) {
          console.log("SW - Sending response from cache");
          return response;
        }
        console.log("SW - Sending Request to Server ");
        return fetch(event.request);
      })
  );
});
